package com.practice;

public class ReverseTraingle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int row=4;
		
		for( int i= row ; i>=1 ;i--){
			for ( int j= i ; j>=1 ;j--)
			{
				System.out.print("*");
			}
			System.out.println();
		}
	

	}
	}




