package practice;

//it is used to reverse the given number from negative to positive and vice versa 
public class UnaryMinusOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x=-5;
		System.out.println("minus of giver number:"+(-x));
		
		int a=1;
		System.out.println(--a);
		System.out.println(a--);
		System.out.println(a);
	}

}
